const kOldValue = Symbol();

module.exports = [{
  file: 'lib/adjectives.js',
  intercept: (adjectives) => {
    return Object.assign(['lab-grown', 'ketchup', 'chili'], { [kOldValue]: adjectives });
  }
}, {
  file: 'lib/utils.js',
  patch: (utils) => {
    utils.toUpperCase = arr => {
      return arr.map(str => {
        const r = str.split('');
        r[0] = r[0].toUpperCase();
        return r.join('');
      });
    };
  }
}];
